import axios from "axios";
import { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import PriceSidebar from "./PriceSidebar";
import Stepper from "./Stepper";
import {
  CardNumberElement,
  CardCvcElement,
  CardExpiryElement,
  useStripe,
  useElements,
} from "@stripe/react-stripe-js";
import { clearErrors, newOrder } from "../../redux/actions/orderAction";
import { useSnackbar } from "notistack";
import { post } from "../../utils/paytmForm";
import FormControl from "@mui/material/FormControl";
import FormControlLabel from "@mui/material/FormControlLabel";
import Radio from "@mui/material/Radio";
import RadioGroup from "@mui/material/RadioGroup";
import MetaData from "../Layouts/MetaData";

import { emptyCart } from "../../redux/actions/cartAction";
import { loadStripe } from "@stripe/stripe-js";

const Payment = ({ stripeApiKey }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { enqueueSnackbar } = useSnackbar();
  const stripe = useStripe();
  const elements = useElements();
  const paymentBtn = useRef(null);

  const [payDisable, setPayDisable] = useState(false);

  const { shippingInfo, cartItems } = useSelector((state) => state.cart);
  const { user } = useSelector((state) => state.user);
  const { error } = useSelector((state) => state.newOrder);

  const totalPrice = cartItems.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  const paymentData = {
    amount: Math.round(totalPrice),
    email: user.email,
    phoneNo: shippingInfo.phoneNo,
  };

  console.log(cartItems);

  const order = {
    shippingInfo,
    orderItems: cartItems,
    totalPrice,
  };

  const makePayment = async () => {
    const stripe = await loadStripe(stripeApiKey);

    const body = {
      products: cartItems,
    };

    const headers = {
      "Content-Type": "application/json",
    };
    const response = await fetch("http://localhost:8000/api/v1/checkout", {
      method: "POST",
      headers: headers,
      body: JSON.stringify(body),
    });

    const session = await response.json();
    const result = stripe.redirectToCheckout({
      sessionId: session.id,
    });
    console.log(result);
    dispatch(newOrder(order));
    dispatch(emptyCart());
    navigate("/order/success");
    if (result.error) {
      enqueueSnackbar(result.error.message, { variant: "error" });
      console.log(result.error);
    }
  };

  useEffect(() => {
    if (error) {
      dispatch(clearErrors());
      enqueueSnackbar(error, { variant: "error" });
    }
  }, [dispatch, error, enqueueSnackbar]);

  return (
    <>
      <MetaData title="Flipkart: Secure Payment | Paytm" />

      <main className="w-full mt-20">
        {/* <!-- row --> */}
        <div className="flex flex-col sm:flex-row gap-3.5 w-full sm:w-11/12 mt-0 sm:mt-4 m-auto sm:mb-7">
          {/* <!-- cart column --> */}
          <div className="flex-1">
            <Stepper activeStep={3}>
              <FormControl>
                <RadioGroup
                  aria-labelledby="payment-radio-group"
                  defaultValue="stripe"
                  name="payment-radio-button"
                >
                  <FormControlLabel
                    value="stripe"
                    control={<Radio />}
                    label={
                      <div className="flex items-center gap-4">
                        <img
                          draggable="false"
                          className="h-6 w-10 object-contain"
                          src="data:image/png;base64,"
                          alt="Paytm Logo"
                        />
                        <span>Stripe</span>
                      </div>
                    }
                  />
                </RadioGroup>
              </FormControl>
              <button
                className={`btn btn-success ${
                  payDisable
                    ? "bg-primary-grey cursor-not-allowed"
                    : "bg-primary-orange cursor-pointer"
                } w-1/2 sm:w-1/4 my-2 py-3 font-medium text-white shadow hover:shadow-lg rounded-sm uppercase outline-none`}
                disabled={payDisable}
                onClick={makePayment}
                type="button"
              >
                Pay â‚¹{totalPrice.toLocaleString()}
              </button>
            </Stepper>
          </div>

          <PriceSidebar cartItems={cartItems} />
        </div>
      </main>
    </>
  );
};

export default Payment;
